import React, { Component } from "react";
import axios from "axios";

export default class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      userPosts: [],
    };
  }

  componentDidMount() {
    // Fetch user-specific posts based on the provided token
    this.fetchUserPosts();
  }

  fetchUserPosts() {
    const { token } = this.props;

    if (token) {
      // Make an API request to get user-specific posts
      axios
        .get("http://localhost:3001/api/post", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((response) => {
          // Update state with user-specific posts
          this.setState({
            userPosts: response.data.posts,
          });
        })
        .catch((error) => {
          console.error("Error fetching user posts:", error);
        });
    }
  }

  render() {
    const { userPosts } = this.state;

    return (
      <div className="profile-container">
        <h1>Profile</h1>
        <div className="user-posts">
          <h2>Your Posts:</h2>
          <ul>
            {userPosts.map((post) => (
              <div className="post-container" key={post.id}>
                <p className="user-post">{post.content}</p>
                <p className="post-date">{post.created_at}</p>
              </div>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}
