import React, { Component } from "react";
import axios from "axios";

export default class Dashboard extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loggedInStatus: " NOT_LOGGED_IN",
      postContent: "",
      posts: [],
    };

    this.handleChange = this.handleChange.bind(this);
    // this.handleSubmit = this.handleSubmit.bind(this);
    this.handleLogoutClick = this.handleLogoutClick.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
  }
  componentDidMount() {
    this.fetchPosts();
  }

  fetchPosts() {
    axios
      .get("http://localhost:3001/api/posts") // Replace with the actual API endpoint for fetching posts
      .then((response) => {
        this.setState({ posts: response.data });
      })
      .catch((error) => {
        console.error("Error fetching posts:", error);
      });
  }

  handlePostContentChange = (e) => {
    this.setState({ postContent: e.target.value });
  };

  handlePostSubmit = () => {
    const { token } = this.props;
    const { postContent } = this.state;

    if (token) {
      // Set up the headers with the Bearer token
      const headers = {
        Authorization: `Bearer ${token}`,
      };

      // Define the post data
      const postData = {
        post: {
          // This structure matches the strong parameters defined in your Rails controller
          content: postContent,
        },
      };

      // Make the API request with the headers
      axios
        .post("http://localhost:3001/api/users/baslat_post", postData, {
          headers,
        })
        .then((response) => {
          // Handle the response, e.g., clear the input field or update the post list
          console.log("Post created successfully!");
          this.setState({ postContent: "" });
          window.location.reload();
        })
        .catch((error) => {
          // Handle the error, possibly unauthorized (401)
          console.error("Error creating post:", error);
        });
    } else {
      // Handle the case where no token is available
      console.error("No token available. User is not authenticated.");
    }
  };

  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value,
    });
  }
  handleLogout() {
    this.setState({
      loggedInStatus: "NOT_LOGGED_IN",
    });
    this.props.history.push("/");
  }
  handleKeyPress = (e) => {
    if (e.key === "Enter") {
      // Call the function to submit the form when Enter is pressed
      this.handlePostSubmit();
    }
  };
  handleLogoutClick() {
    axios
      .delete("http://localhost:3001/logout", { withCredentials: true })
      .then((response) => {
        localStorage.removeItem("token");
        this.handleLogout();
      })
      .catch((error) => {
        console.log("Logout error", error);
      });
  }
  render() {
    return (
      <div className="div-main">
        <h1>Dashboard</h1>
        <h1>Status: {this.props.loggedInStatus}</h1>
        <div className="div-post">
          <h2 id="h2-post">Post</h2>
          <input
            type="text"
            className="input-post"
            required
            placeholder="Write your post"
            value={this.state.postContent}
            onChange={this.handlePostContentChange}
            onKeyPress={this.handleKeyPress}
          />
          <button
            onClick={this.handlePostSubmit}
            type="submit"
            className="button-52"
          >
            Send
          </button>
        </div>
        <button
          onClick={() => this.handleLogoutClick()}
          type="submit"
          className="button-52"
        >
          Logout
        </button>
        <div>
          <h2>Posts</h2>
          <ul>
            {this.state.posts.map((post) => (
              <li key={post.id}>{post.content}</li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}
