import React, { Component } from "react";
import axios from "axios";

export default class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loggedInStatus: "You are not logged in.",
      userPosts: [],
      userId: null,
    };
  }

  componentDidMount() {
    const { token } = this.props;
    console.log("Token from props:", token);
    this.fetchUserPosts();
  }

  fetchUserPosts() {
    // Modify the URL to include the user ID as a query parameter
    axios
      .get("http://localhost:3001/api/posts", {})
      .then((response) => {
        const { userId, userPosts } = response.data;
        this.setState({ userId, userPosts });
      })
      .catch((error) => {
        console.error("Error fetching user posts:", error);
      });
  }

  render() {
    const { userPosts } = this.state;

    return (
      <div className="profile-container">
        <h1>Profile</h1>
        <div className="user-posts">
          <ul>
            {userPosts.map((post) => (
              <div className="user-post" key={post.id}>
                <p>{post.content}</p>
              </div>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}
