import React, { Component } from "react";
import axios from "axios";
import { Redirect } from "react-router-dom"; // Import Redirect component if using React Router

export default class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: "",
      loginErrors: "",
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value,
    });
  }

  handleSubmit(event) {
    event.preventDefault();
    const { email, password } = this.state;
    console.log(password);

    axios
      .post(
        "http://localhost:3001/sessions",
        {
          user: {
            email: email,
            password: password,
          },
        },
        { withCredentials: true }
      )
      .then((response) => {
        if (response.data.logged_in && response.data.patient) {
          this.props.loggedInStatus(response.data.user); // Assuming handleSuccessfulAuth sets user's login status
        } else {
          console.log("Login failed");
        }
      })
      .catch((error) => {
        console.log("Login error", error);
      });
  }

  render() {
    if (this.props.loggedInStatus) {
      return <Redirect to="/dashboard" />; // Redirect to dashboard if already logged in
    }

    return (
      <div>
        <form onClick={this.handleSubmit}>
          <div className="form-group">
            <input
              className="form-control"
              type="email"
              name="email"
              placeholder="Email"
              required
              value={this.state.email}
              onChange={this.handleChange}
            />
          </div>
          <div className="form-group">
            <input
              className="form-control"
              type="password"
              name="password"
              placeholder="Password"
              required
              value={this.state.password}
              onChange={this.handleChange}
            />
          </div>
          <button onClick={this.handleSubmit} type="submit">
            Login
          </button>
        </form>
      </div>
    );
  }
}
