import React, { Component } from "react";
import axios from "axios";

export default class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      userPosts: [],
      userId: null,
    };
  }

  componentDidMount() {
    const storedUserData = localStorage.getItem("userData");
    const userData = storedUserData ? JSON.parse(storedUserData) : null;

    if (userData) {
      const loggedInUserId = userData.id; // Access the user ID from the user data
      console.log("Logged-in User ID:", loggedInUserId);
      this.fetchUserPosts(loggedInUserId); // Fetch user posts using the ID
    }
  }

  fetchUserPosts() {
    const { token } = this.props;

    if (token) {
      axios
        .get("http://localhost:3001/api/posts") // Replace with your API endpoint
        .then((response) => {
          const loggedInUserId = response.userId;
          console.log(token);
          console.log("logged in userid", loggedInUserId);
          // Filter posts to get only the ones belonging to the logged-in user
          const userPosts = response.data.filter(
            (post) => post.user_id === loggedInUserId
          );

          this.setState({ userPosts });
        })
        .catch((error) => {
          console.error("Error fetching user posts:", error);
        });
    }
  }

  render() {
    const { userPosts } = this.state;

    return (
      <div className="profile-container">
        <h1>Profile</h1>
        <div className="user-posts">
          <h2>Your Posts:</h2>
          <ul>
            {userPosts.map((post) => (
              <div
                className={`post-container post-${post.user.id}-${post.id}`}
                key={post.id}
              >
                <p className="user-post">{post.content}</p>
              </div>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}
