import React, { Component } from "react";
import axios from "axios";

export default class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      userPosts: [],
      userId: null,
    };
  }

  componentDidMount() {
    this.fetchUserPosts();
    this.checkUserId();
  }
  checkUserId() {
    const { email, password } = this.state;
    console.log("Logging in with:", email, password);

    axios
      .post(
        "http://localhost:3001/sessions",
        {
          user: {
            email: email,
            password: password,
          },
        },
        {
          withCredentials: true,
        }
      )
      .then((response) => {
        console.log("Login response:", response.data);
        const loggedInUserId = response.data.user.id;
        console.log(loggedInUserId);
        const userPosts = response.data.filter(
          (post) => post.user_id === loggedInUserId
        );

        this.setState({ userPosts });
      })
      .catch((error) => {
        console.log("Login error", error);
        this.setState({
          loginError: "An error occurred during login. Please try again.",
        });
      });
  }

  fetchUserPosts() {
    const { token } = this.props;

    if (token) {
      axios
        .get("http://localhost:3001/api/posts") // Replace with your API endpoint
        .then((response) => {
          const loggedInUserId = response.userId;
          console.log(token);
          console.log("logged in userid", loggedInUserId);
          // Filter posts to get only the ones belonging to the logged-in user
          const userPosts = response.data.filter(
            (post) => post.user_id === loggedInUserId
          );

          this.setState({ userPosts });
        })
        .catch((error) => {
          console.error("Error fetching user posts:", error);
        });
    }
  }

  render() {
    const { userPosts } = this.state;

    return (
      <div className="profile-container">
        <h1>Profile</h1>
        <div className="user-posts">
          <h2>Your Posts:</h2>
          <ul>
            {userPosts.map((post) => (
              <div
                className={`post-container post-${post.user.id}-${post.id}`}
                key={post.id}
              >
                <p className="user-post">{post.content}</p>
              </div>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}
