// profile.js
import React, { Component } from "react";
import axios from "axios";

export default class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      user: null, // Initialize as null
      userPosts: [], // Initialize as an empty array
    };
  }

  componentDidMount() {
    const token = localStorage.getItem("token"); // Retrieve the token from localStorage

    if (token) {
      // Set up the headers with the Bearer token
      const headers = {
        Authorization: `Bearer ${token}`,
      };

      // Make the API request with the headers
      axios
        .get("http://localhost:3001/api/profile", { headers })
        .then((response) => {
          // Handle the response and set the user data in the state
          this.setState({ user: response.data });
          axios
            .get("http://localhost:3001/api/posts", { headers })
            .then((postsResponse) => {
              // Filter posts to get only the user's posts
              const userPosts = postsResponse.data.filter(
                (post) => post.user.id === response.data.user_id
              );
              console.log("User Posts:", userPosts); // Log user's posts

              this.setState({ userPosts });
            })
            .catch((error) => {
              console.error("Error fetching user's posts:", error);
            });
        })
        .catch((error) => {
          // Handle any errors, e.g., unauthorized (401)
          console.error("Error fetching user profile:", error);
        });
    } else {
      // Handle the case where no token is available
      console.error("No token available. User is not authenticated.");
    }
  }

  render() {
    const { user, userPosts } = this.state;

    if (!user) {
      return <div>Loading...</div>; // Render a loading message while fetching the profile
    }
    console.log("User Posts in Render:", userPosts); // Log userPosts

    // Render the user profile information
    return (
      <div>
        <h1>User Profile</h1>
        <p>Name: {user.name}</p>
        <p>Email: {user.email}</p>
        <h2>User Posts</h2>
        {userPosts
          .filter((post) => post.user.id === this.props.loggedInUserId)
          .map((post) => (
            <div
              className={`post-container post-${userPosts.user.id}-${post.id}`}
              key={post.id}
            >
              <p className="user-post">{post.content}</p>
            </div>
          ))}
      </div>
    );
  }
}
