import React, { Component } from "react";
import axios from "axios";

export default class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loggedInStatus: "You are not logged in.",
      userPosts: {},
      user_id: null, // Initialize userId
    };
  }

  componentDidMount() {
    const { token } = this.props;
    console.log("Token in Profile component:", token); // Add this line

    if (token) {
      // Decode the JWT token to extract user information
      const decodedToken = this.decodeJwtToken(token);

      if (decodedToken) {
        // Assuming the token contains a field like 'userId'
        const user_id = decodedToken.user_id;

        // Now that you have the user ID, you can fetch user-specific data
        this.fetchUserPosts(user_id);
      }
    }
  }
  decodeJwtToken(token) {
    try {
      // Decode the JWT token (you may need to use a library for this)
      const base64Url = token.split(".")[1];
      const base64 = base64Url.replace(/-/g, "+").replace(/_/g, "/");
      const jsonPayload = decodeURIComponent(
        atob(base64)
          .split("")
          .map(function (c) {
            return "%" + ("00" + c.charCodeAt(0).toString(16)).slice(-2);
          })
          .join("")
      );

      return JSON.parse(jsonPayload);
    } catch (error) {
      console.error("Error decoding JWT token:", error);
      return null;
    }
  }

  fetchUserPosts() {
    // Modify the URL to include the user ID as a query parameter
    axios
      .get("http://localhost:3001/api/posts", {
        headers: {
          Authorization: `Bearer ${this.props.token}`, // Pass the token in the header
        },
      })
      .then((response) => {
        console.log("Response data:", response.data); // Log the API response data

        const userPosts = response.data;
        response.data.forEach((post) => {
          const user_id = post.user.id;
          console.log("userId:", user_id); // Add this line
          console.log("userPosts:", userPosts); // Add this line

          if (!userPosts[user_id]) {
            userPosts[user_id] = {
              user: post.user,
              posts: [],
            };
          }

          userPosts[user_id].posts.push(post);
        });
        this.setState({ userPosts });
      })
      .catch((error) => {
        console.error("Error fetching user posts:", error);
      });
  }

  render() {
    const { userPosts } = this.state;

    return (
      <div className="profile-container">
        <h1>Profile</h1>
        <div className="user-posts">
          {Object.entries(userPosts).map(([user_id, userData]) => (
            <div className="user-post-container" key={user_id}>
              <h2 className="user-name-header">User {user_id}'s Posts:</h2>
              <ul>
                {userData.posts.map((post) => (
                  <div
                    className={`post-container post-${userData.user.id}-${post.id}`}
                    key={post.id}
                  >
                    <p className="user-post">{post.content}</p>
                  </div>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>
    );
  }
}
