import React, { Component } from "react";
import axios from "axios";

export default class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loggedInStatus: "You are not logged in.",
      userPosts: [],
      userId: null, // Initialize userId as null
    };
  }

  componentDidMount() {
    const { token } = this.props;
    console.log("Token from props:", token);
    this.fetchUserPosts();
    this.retrieveUserId(); // Call this function when the user logs in.
  }
  retrieveUserId() {
    // Assuming you have a way to fetch the user's ID after authentication
    const { token } = this.props;
    axios
      .get("http://localhost:3001/api/users", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        const userId = response.data.id;
        this.setState({ userId }, () => {
          // After setting the userId, fetch user posts
          this.fetchUserPosts();
        });
      })
      .catch((error) => {
        console.error("Error fetching user ID:", error);
      });
  }

  fetchUserPosts() {
    const { token, userId } = this.props;
    console.log("Token from props:", token);

    // Modify the URL to include the user ID as a query parameter
    axios
      .get(`http://localhost:3001/api/posts?userId=${userId}`)
      .then((response) => {
        const userPosts = response.data;
        this.setState({ userPosts });
      })
      .catch((error) => {
        console.error("Error fetching user posts:", error);
      });
  }

  render() {
    const { userPosts } = this.state;

    return (
      <div className="profile-container">
        <h1>Profile</h1>
        <div className="user-posts">
          <ul>
            {userPosts.map((post) => (
              <div className="user-post" key={post.id}>
                <p>{post.content}</p>
              </div>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}
