import React, { Component } from "react";
import axios from "axios";

export default class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      loggedInStatus: "You are not logged in.",
      userPosts: [],
    };
  }

  componentDidMount() {
    const { token } = this.props;
    console.log("Token from props:", token);
    this.fetchUserPosts();
  }

  fetchUserPosts() {
    const { token } = this.props;

    if (token) {
      // Set up the headers with the Bearer token
      const headers = {
        Authorization: `B ${token}`,
      };

      axios
        .get("http://localhost:3001/api/posts", { headers })
        .then((response) => {
          const userPosts = response.data;
          this.setState({ userPosts });
        })
        .catch((error) => {
          console.error("Error fetching user posts:", error);
        });
    } else {
      // Handle the case where no token is available
      console.error("No token available. User is not authenticated.");
    }
  }

  render() {
    const { userPosts } = this.state;

    return (
      <div className="profile-container">
        <h1>Profile</h1>
        <div className="user-posts">
          <h2>Your Posts</h2>
          <ul>
            {userPosts.map((post) => (
              <div className="user-post" key={post.id}>
                <p>{post.content}</p>
              </div>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}
