import React, { Component } from "react";
import axios from "axios";

export default class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      userPosts: {},
    };
  }

  componentDidMount() {
    this.fetchUserPosts();
  }

  fetchUserPosts() {
    const { token } = this.props;

    if (token) {
      axios
        .get("http://localhost:3001/api/posts") // Replace with your API endpoint
        .then((response) => {
          const userPosts = {};

          response.data.forEach((post) => {
            const userId = post.user.id;

            if (userId === token.userId) {
              if (!userPosts[userId]) {
                userPosts[userId] = {
                  user: post.user,
                  posts: [],
                };
              }

              userPosts[userId].posts.push(post);
            }
          });

          this.setState({ userPosts });
        })
        .catch((error) => {
          console.error("Error fetching user posts:", error);
        });
    }
  }

  render() {
    const { userPosts } = this.state;

    return (
      <div className="profile-container">
        <h1>Profile</h1>
        <div className="user-posts">
          <h2>Your Posts:</h2>
          {Object.entries(userPosts).map(([userId, userData]) => (
            <div className="user-post-container" key={userId}>
              <h2 className="user-name-header">User {userId}'s Posts:</h2>
              <ul>
                {userData.posts.map((post) => (
                  <div
                    className={`post-container post-${userData.user.id}-${post.id}`}
                    key={post.id}
                  >
                    <p className="user-post">{post.content}</p>
                  </div>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>
    );
  }
}
