import React, { Component } from "react";
import { BrowserRouter, Switch, Route, Redirect } from "react-router-dom";
import Dashboard from "./dashboard";
import Home from "./home";
import Registration from "./auth/registration";

export default class App extends Component {
  constructor() {
    super();

    this.state = {
      loggedInStatus: "NOT_LOGGED_IN",
      token: null, // Added to store the JWT token
    };

    this.handleLogin = this.handleLogin.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
    this.handleSuccessfulAuth = this.handleSuccessfulAuth.bind(this);
  }

  componentDidMount() {
    // Check if a token is already stored in localStorage
    const storedToken = localStorage.getItem("token");
    if (storedToken) {
      this.setState({
        loggedInStatus: "LOGGED_IN",
        token: storedToken,
      });
    }
  }

  handleSuccessfulAuth(token) {
    this.handleLogin(token);
  }

  handleLogin(token) {
    console.log("Setting loggedInStatus to LOGGED_IN...");
    // Store the token in the state and localStorage
    this.setState({
      loggedInStatus: "LOGGED_IN",
      token: token,
    });
    localStorage.setItem("token", token);
    window.location.reload();
  }

  handleLogout() {
    console.log("Logging out...");
    // Remove the token from the state and localStorage
    this.setState({
      loggedInStatus: "NOT_LOGGED_IN",
      token: null,
    });
    localStorage.removeItem("token");
  }

  render() {
    return (
      <div className="app">
        <h1>Masiro</h1>
        <BrowserRouter>
          <Switch>
            <Route
              exact
              path={"/"}
              render={(props) => (
                <Home
                  {...props}
                  loggedInStatus={this.state.loggedInStatus}
                  handleLogin={this.handleLogin}
                  handleLogout={this.handleLogout}
                />
              )}
            />
            {this.state.loggedInStatus === "LOGGED_IN" && (
              <Redirect exact from="/" to="/dashboard" />
            )}
            <Route
              exact
              path={"/dashboard"}
              render={(props) => (
                <Dashboard
                  {...props}
                  loggedInStatus={this.state.loggedInStatus}
                  token={this.state.token} // Pass the token to the dashboard
                />
              )}
            />
            <Route
              exact
              path={"/registration"}
              render={(props) => (
                <Registration
                  handleSuccessfulAuth={this.handleSuccessfulAuth}
                  {...props}
                  loggedInStatus={this.state.loggedInStatus}
                />
              )}
            />
          </Switch>
        </BrowserRouter>
      </div>
    );
  }
}
