import React, { Component } from "react";
import axios from "axios";

export default class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      user: null, // Initialize as null
    };
  }

  componentDidMount() {
    const token = localStorage.getItem("token"); // Retrieve the token from localStorage

    if (token) {
      // Set up the headers with the Bearer token
      const headers = {
        Authorization: `Bearer ${token}`,
      };

      // Make the API request with the headers
      axios
        .get("http://localhost:3001/api/profile", { headers })
        .then((response) => {
          // Handle the response and set the user data in the state
          this.setState({ user: response.data.user });
        })
        .catch((error) => {
          // Handle any errors, e.g., unauthorized (401)
          console.error("Error fetching user profile:", error);
        });
    } else {
      // Handle the case where no token is available
      console.error("No token available. User is not authenticated.");
    }
  }

  render() {
    const { user } = this.state;

    if (!user) {
      return <div>Loading...</div>; // Render a loading message while fetching the profile
    }

    // Render the user profile information
    return (
      <div>
        <h1>User Profile</h1>
        <p>Name: {user.name}</p>
        <p>Email: {user.email}</p>
        <h2>User Posts</h2>
        <ul>
          {user.posts.map((post) => (
            <li key={post.id}>{post.content}</li>
          ))}
        </ul>
      </div>
    );
  }
}
