import React, { Component } from "react";
import axios from "axios";

class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      user: null, // Initialize as null
    };
  }

  componentDidMount() {
    // Fetch user profile data when the component mounts
    this.fetchUserProfile();
  }

  fetchUserProfile() {
    const { token } = this.props;

    if (token) {
      // Set up the headers with the Bearer token
      const headers = {
        Authorization: `Bearer ${token}`,
      };

      // Make an API request to fetch the user's profile
      axios
        .get("http://localhost:3001/api/profile", { headers })
        .then((response) => {
          // Handle the response and set the user data in the state
          this.setState({ user: response.data });
        })
        .catch((error) => {
          // Handle any errors, e.g., unauthorized (401)
          console.error("Error fetching user profile:", error);
        });
    } else {
      // Handle the case where no token is available
      console.error("No token available. User is not authenticated.");
    }
  }

  render() {
    const { user } = this.state;

    if (!user) {
      return <div>Loading...</div>; // You can replace this with a loading indicator
    }

    return (
      <div className="profile-container">
        <h1>User Profile</h1>
        <div className="profile-info">
          <img src={user.profile_picture_url} alt="Profile" />
          <h2>{user.username}</h2>
          <p>Email: {user.email}</p>
          {/* Add other user profile information as needed */}
        </div>
        {/* Display user's posts or other profile-related information */}
      </div>
    );
  }
}

export default Profile;
