  //tokeni localstorage'dan okuyup token varsa loggedini ayarla
  checkLoginStatus() {
    localStorage.getItem("token");
  }

  handleSuccessfulAuth(data) {
    this.handleLogin();
  }

  componentDidMount() {
    this.checkLoginStatus();
  }

  handleLogin() {
    console.log("Setting loggedInStatus to LOGGED_IN...");
    this.setState({
      loggedInStatus: "LOGGED_IN",
    });
  }

  handleLogout() {
    this.setState({
      loggedInStatus: "NOT_LOGGED_IN",
    });
  }

  render() {
    return (
      <div className="app">
        <h1>Masiro</h1>
        <BrowserRouter>
          <Switch>
            {/* <Route
              exact
              path={"/login"}
              render={(props) => (
                <Login
                  {...props}
