import React, { Component } from "react";
import Login from "./auth/Login";
import { Link } from "react-router-dom";

export default class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isOpen: false,
    };
    this.handleSuccessfulAuth = this.handleSuccessfulAuth.bind(this);
  }
  componentDidMount() {
    // Check if a token is already stored in the App component's state
    const storedToken = localStorage.getItem("token");

    if (storedToken) {
      // Redirect to the dashboard if the user is logged in
      this.props.history.push("/dashboard");
    }
  }

  handleSuccessfulAuth(data) {
    this.props.handleLogin(data); // Pass the data received from the login response
    this.props.history.push("/dashboard");
    console.log("pushing to dashboard.");
  }
  handleRegistartion() {
    window.location.reload();
  }
  togglePopup = () => {
    this.setState((prevState) => ({
      isOpen: !prevState.isOpen, // Toggle the pop-up state
    }));
  };
  render() {
    return (
      <div>
        <h2>Home</h2>
        <h3>Status: {this.props.loggedInStatus}</h3>
        <Login handleSuccessfulAuth={this.handleSuccessfulAuth} />
        <p onClick={this.handleRegistartion}>
          Don't have an account? <Link to="/registration">Register</Link>
        </p>
      </div>
    );
  }
}
